import React from 'react'
import { useState } from 'react'
const login = () => {
  const [credential, setCredential] = useState({email: "", password: ""})  
    const handleSubmit=async(e)=>{
      e.preventDefault();
      const response = await fetch(`http://localhost:5000/api/auth/login`, {
        method: 'POST', 

        headers: {
          'Content-Type': 'application/json',

        },

      body: JSON.stringify({email:credential.email,password:credential.password}) 
    });
    const json=await response.json;
    console.log(json)
}

const onChange = (e)=>{
  setCredential({...credential, [e.target.name]: e.target.value})
}

  return (
    <div>
        <form onSubmit={handleSubmit}>
  <div className="mb-3">
    <label htmlFor="email" className="form-label">Email address</label>
    <input type="email" className="form-control" id="email" name="email" value={credential.email} onChange={onChange} aria-describedby="emailHelp"/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div className="mb-3">
    <label htmlFor="password" className="form-label">Password</label>
    <input type="password" className="password" id="password" name="password" value={credential.password} onChange={onChange}/>
  </div>

  <button type="submit" className="btn btn-primary" >Submit</button>
</form>
    </div>
  )
}

export default login
